mapper.update:
update board
set reply_cnt = reply_cnt + #{amount}
where board_no = #{boardNo}

update board
set view_cnt = view_cnt + 1
where board_no = #{boardNo}

update board set board_title=#{boardTitle},
                 board_contents=#{boardContents},
                 user_id=#{userId}
where board_no=#{boardNo}
